import React, { useState, useEffect } from 'react';

const IceCreamTracker = () => {
  const [entries, setEntries] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({
    shop: 'Joelato',
    flavors: [''],
    date: new Date().toISOString().split('T')[0],
    notes: '',
    person: ''
  });
  const [filter, setFilter] = useState('all');
  const [currentTime, setCurrentTime] = useState(new Date());
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    loadEntries();
    const timer = setInterval(() => setCurrentTime(new Date()), 1000);
    return () => clearInterval(timer);
  }, []);

  const loadEntries = async () => {
    setIsLoading(true);
    setError('');
    try {
      const response = await fetch('http://localhost:4000/entries');
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      setEntries(data);
    } catch (error) {
      console.error('Error loading entries from API:', error);
      setError('Could not connect to database. Using local storage.');
      
      // Fallback to localStorage
      try {
        const result = await window.storage?.get('ice-cream-entries');
        if (result) {
          setEntries(JSON.parse(result.value));
        }
      } catch (localError) {
        console.log('No existing entries found');
      }
    } finally {
      setIsLoading(false);
    }
  };

  // Add new flavor field
  const addFlavorField = () => {
    setFormData({
      ...formData,
      flavors: [...formData.flavors, '']
    });
  };

  // Remove flavor field
  const removeFlavorField = (index) => {
    const newFlavors = [...formData.flavors];
    newFlavors.splice(index, 1);
    setFormData({
      ...formData,
      flavors: newFlavors
    });
  };

  // Update specific flavor
  const updateFlavor = (index, value) => {
    const newFlavors = [...formData.flavors];
    newFlavors[index] = value;
    setFormData({
      ...formData,
      flavors: newFlavors
    });
  };

  const handleSubmit = async () => {
    // Filter out empty flavors
    const nonEmptyFlavors = formData.flavors.filter(flavor => flavor.trim() !== '');
    
    if (nonEmptyFlavors.length === 0 || !formData.person) {
      setError('Please fill in at least one flavor and your name');
      return;
    }
    
    setIsLoading(true);
    setError('');
    
    // Create one entry per flavor
    const entriesToSave = nonEmptyFlavors.map(flavor => ({
      shop: formData.shop,
      flavor: flavor.trim(),
      date: formData.date,
      notes: formData.notes,
      person: formData.person,
      timestamp: new Date().toISOString()
    }));
    
    try {
      // Save each flavor entry
      const savedEntries = [];
      for (const entry of entriesToSave) {
        const response = await fetch('http://localhost:4000/entries', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(entry),
        });
        
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        
        const savedEntry = await response.json();
        savedEntries.push(savedEntry);
      }
      
      // Update UI with all saved entries
      const updatedEntries = [...savedEntries, ...entries];
      setEntries(updatedEntries);
      
      // Keep local backup
      try {
        if (window.storage) {
          await window.storage.set('ice-cream-entries', JSON.stringify(updatedEntries));
        }
      } catch (storageError) {
        console.log('Could not save to local storage:', storageError);
      }
      
      // Reset form
      setFormData({
        shop: 'Joelato',
        flavors: [''],
        date: new Date().toISOString().split('T')[0],
        notes: '',
        person: ''
      });
      setShowForm(false);
      
    } catch (error) {
      console.error('Error saving entries to API:', error);
      setError('Failed to save to database. Saving locally.');
      
      // Fallback to local storage
      const localEntries = entriesToSave.map((entry, index) => ({
        ...entry,
        id: Date.now() + index
      }));
      
      const updatedEntries = [...localEntries, ...entries];
      setEntries(updatedEntries);
      
      try {
        if (window.storage) {
          await window.storage.set('ice-cream-entries', JSON.stringify(updatedEntries));
        }
      } catch (storageError) {
        console.log('Could not save to local storage:', storageError);
      }
      
      setShowForm(false);
    } finally {
      setIsLoading(false);
    }
  };

  const deleteEntry = async (id) => {
    if (!window.confirm('Are you sure you want to delete this entry?')) return;
    
    setIsLoading(true);
    try {
      const response = await fetch(`http://localhost:4000/entries/${id}`, {
        method: 'DELETE',
      });
      
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      
      const updatedEntries = entries.filter(e => e.id !== id);
      setEntries(updatedEntries);
      
      // Update local backup
      try {
        if (window.storage) {
          await window.storage.set('ice-cream-entries', JSON.stringify(updatedEntries));
        }
      } catch (storageError) {
        console.log('Could not update local storage:', storageError);
      }
      
    } catch (error) {
      console.error('Error deleting entry from API:', error);
      setError('Failed to delete from database. Deleting locally.');
      
      // Fallback to local delete
      const updatedEntries = entries.filter(e => e.id !== id);
      setEntries(updatedEntries);
      
      try {
        if (window.storage) {
          await window.storage.set('ice-cream-entries', JSON.stringify(updatedEntries));
        }
      } catch (storageError) {
        console.log('Could not update local storage:', storageError);
      }
    } finally {
      setIsLoading(false);
    }
  };

  const filteredEntries = entries.filter(e => {
    if (filter === 'all') return true;
    return e.shop === filter;
  });

  const formatTime = (date) => {
    return date.toLocaleTimeString('en-US', { 
      hour12: false,
      hour: '2-digit',
      minute: '2-digit',
      second: '2-digit'
    });
  };

  // Group entries by date for better display
  const groupedEntries = filteredEntries.reduce((groups, entry) => {
    const date = entry.date;
    if (!groups[date]) {
      groups[date] = [];
    }
    groups[date].push(entry);
    return groups;
  }, {});

  // Sort dates descending
  const sortedDates = Object.keys(groupedEntries).sort((a, b) => new Date(b) - new Date(a));

  return (
    <div className="min-h-screen bg-black text-cyan-400 p-0 m-0" style={{fontFamily: "'Courier New', monospace", fontSize: '16px', lineHeight: '1.2'}}>
      <style>{`
        @import url('https://fonts.googleapis.com/css2?family=IBM+Plex+Mono:wght@400;500&display=swap');
        
        body {
          margin: 0;
          padding: 0;
          overflow-x: hidden;
        }
        .dos-input {
          background: black;
          color: #00ff00;
          border: 1px solid #008800;
          outline: none;
          font-family: 'IBM Plex Mono', monospace;
          font-size: 16px;
          padding: 2px 4px;
        }
        .dos-input:focus {
          border-color: #00ff00;
          box-shadow: 0 0 5px #00ff00;
        }
        .dos-input::placeholder {
          color: #008800;
        }
        .dos-select {
          background: black;
          color: #00ff00;
          border: 1px solid #008800;
          outline: none;
          font-family: 'IBM Plex Mono', monospace;
          font-size: 16px;
          padding: 2px 4px;
        }
        .dos-textarea {
          background: black;
          color: #00ff00;
          border: 1px solid #008800;
          outline: none;
          font-family: 'IBM Plex Mono', monospace;
          font-size: 16px;
          padding: 4px;
          resize: vertical;
          min-height: 60px;
        }
        .blink {
          animation: blink 1s infinite;
        }
        @keyframes blink {
          0%, 49% { opacity: 1; }
          50%, 100% { opacity: 0; }
        }
        .scanline {
          position: relative;
          overflow: hidden;
        }
        .scanline::after {
          content: '';
          position: absolute;
          top: 0;
          left: 0;
          right: 0;
          height: 2px;
          background: linear-gradient(to bottom, transparent, rgba(0, 255, 0, 0.2), transparent);
          animation: scan 3s linear infinite;
        }
        @keyframes scan {
          0% { transform: translateY(-100%); }
          100% { transform: translateY(100vh); }
        }
        .flavor-tag {
          display: inline-block;
          background: #004400;
          color: #00ff00;
          padding: 2px 8px;
          margin: 2px;
          border-radius: 3px;
          border: 1px solid #008800;
        }
      `}</style>

      <div className="p-4 scanline">
        {/* Status Bar */}
        {error && (
          <div className="mb-3 p-2 bg-red-900 text-red-300 border border-red-700">
            <div className="flex justify-between">
              <span>âš ï¸ {error}</span>
              <button onClick={() => setError('')} className="text-red-400">[X]</button>
            </div>
          </div>
        )}

        {isLoading && (
          <div className="mb-3 p-2 bg-blue-900 text-blue-300 border border-blue-700">
            <div className="flex items-center gap-2">
              <span className="animate-pulse">â³</span>
              <span>Processing...</span>
            </div>
          </div>
        )}

        {/* Header */}
        <div className="mb-4">
          <div className="text-yellow-400">
            â•”â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•—
          </div>
          <div className="text-yellow-400 flex justify-between">
            <span>â•‘  GELATO DATABASE v2.0 - MULTI-FLAVOR EDITION</span>
            <span> {formatTime(currentTime)} â•‘</span>
          </div>
          <div className="text-yellow-400">
            â•šâ•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
          </div>
        </div>

        {/* Database Info */}
        <div className="mb-4 text-green-400">
          <div>C:\GELATO\DATABASE&gt; STATUS</div>
          <div className="ml-4">
            Database: SQLite (icecream.db)
            <br />
            Backend: http://localhost:4000
            <br />
            Total Entries: {entries.length}
            <br />
            Unique Dates: {sortedDates.length}
            <br />
            Filter: {filter.toUpperCase()}
            <br />
            <br />
            {isLoading ? (
              <span className="text-yellow-400">[SYNCING WITH DATABASE...]</span>
            ) : (
              <span className="text-green-400">[DATABASE CONNECTION ACTIVE]</span>
            )}
          </div>
        </div>

        {/* Directory Listing */}
        <div className="mb-4 text-green-400">
          <div>C:\GELATO&gt; DIR</div>
          <div className="ml-4">
            Volume in drive C is GELATO-DB
            <br />
            Volume Serial Number is G3L-470
            <br />
            <br />
            Directory of C:\GELATO
            <br />
            <br />
            {sortedDates.length} Date(s)           {entries.length} Flavor(s)
            <br />
            0 Dir(s)   999,999,999 bytes free
          </div>
        </div>

        {/* Menu */}
        <div className="mb-4">
          <div className="text-white">
            C:\GELATO&gt; MENU
          </div>
          <div className="ml-4 mt-2 flex flex-wrap gap-2">
            <button
              onClick={() => setShowForm(!showForm)}
              className={`px-3 py-1 ${showForm ? 'bg-cyan-400 text-black' : 'text-cyan-400 hover:text-white hover:bg-cyan-900'} border border-cyan-700`}
              disabled={isLoading}
            >
              [N]EW TASTING
            </button>
            <button
              onClick={() => setFilter('all')}
              className={`px-3 py-1 ${filter === 'all' ? 'bg-cyan-400 text-black' : 'text-cyan-400 hover:text-white hover:bg-cyan-900'} border border-cyan-700`}
              disabled={isLoading}
            >
              [A]LL
            </button>
            <button
              onClick={() => setFilter('Joelato')}
              className={`px-3 py-1 ${filter === 'Joelato' ? 'bg-cyan-400 text-black' : 'text-cyan-400 hover:text-white hover:bg-cyan-900'} border border-cyan-700`}
              disabled={isLoading}
            >
              [J]OELATO
            </button>
            <button
              onClick={() => setFilter("Mary's Milk Bar")}
              className={`px-3 py-1 ${filter === "Mary's Milk Bar" ? 'bg-cyan-400 text-black' : 'text-cyan-400 hover:text-white hover:bg-cyan-900'} border border-cyan-700`}
              disabled={isLoading}
            >
              [M]ARYS
            </button>
            <button
              onClick={loadEntries}
              className="px-3 py-1 text-green-400 hover:text-white hover:bg-green-900 border border-green-700"
              disabled={isLoading}
            >
              [R]EFRESH
            </button>
          </div>
        </div>

        {/* New Entry Form */}
        {showForm && (
          <div className="border border-cyan-400 p-4 mb-4 bg-gray-900">
            <div className="text-yellow-400 mb-3">
              â•â•â• NEW GELATO TASTING â•â•â•
            </div>
            
            <div className="space-y-3">
              <div className="flex items-center gap-2">
                <span className="text-green-400 w-32">SHOP:</span>
                <select
                  value={formData.shop}
                  onChange={(e) => setFormData({...formData, shop: e.target.value})}
                  className="dos-select flex-1"
                  disabled={isLoading}
                >
                  <option value="Joelato">JOELATO</option>
                  <option value="Mary's Milk Bar">MARYS MILK BAR</option>
                  <option value="Other">OTHER</option>
                </select>
              </div>

              <div className="flex items-center gap-2">
                <span className="text-green-400 w-32">NAME:</span>
                <input
                  type="text"
                  value={formData.person}
                  onChange={(e) => setFormData({...formData, person: e.target.value})}
                  className="dos-input flex-1"
                  placeholder="YOUR NAME"
                  disabled={isLoading}
                />
              </div>

              <div className="flex items-center gap-2">
                <span className="text-green-400 w-32">DATE:</span>
                <input
                  type="date"
                  value={formData.date}
                  onChange={(e) => setFormData({...formData, date: e.target.value})}
                  className="dos-input"
                  disabled={isLoading}
                />
              </div>

              {/* Multiple Flavors Section */}
              <div className="space-y-2">
                <div className="flex items-center justify-between">
                  <span className="text-green-400">FLAVORS:</span>
                  <button
                    type="button"
                    onClick={addFlavorField}
                    className="text-cyan-400 hover:text-white px-2 py-1 border border-cyan-700"
                    disabled={isLoading}
                  >
                    [+ ADD FLAVOR]
                  </button>
                </div>
                
                {formData.flavors.map((flavor, index) => (
                  <div key={index} className="flex items-center gap-2">
                    <span className="text-cyan-400 w-8">{index + 1}.</span>
                    <input
                      type="text"
                      value={flavor}
                      onChange={(e) => updateFlavor(index, e.target.value)}
                      className="dos-input flex-1"
                      placeholder={`FLAVOR ${index + 1}`}
                      disabled={isLoading}
                    />
                    {formData.flavors.length > 1 && (
                      <button
                        type="button"
                        onClick={() => removeFlavorField(index)}
                        className="text-red-400 hover:text-red-300 px-2 border border-red-700"
                        disabled={isLoading}
                      >
                        [X]
                      </button>
                    )}
                  </div>
                ))}
              </div>

              <div className="flex gap-2">
                <span className="text-green-400 w-32 pt-2">NOTES:</span>
                <textarea
                  value={formData.notes}
                  onChange={(e) => setFormData({...formData, notes: e.target.value})}
                  className="dos-textarea flex-1"
                  placeholder="OPTIONAL NOTES ABOUT THE TASTING"
                  disabled={isLoading}
                />
              </div>

              <div className="flex gap-4 mt-4">
                <button
                  onClick={handleSubmit}
                  className="px-6 py-2 text-black bg-green-400 hover:bg-green-500 border border-green-700 disabled:opacity-50 disabled:cursor-not-allowed"
                  disabled={isLoading || formData.flavors.filter(f => f.trim() !== '').length === 0 || !formData.person}
                >
                  {isLoading ? '[S]AVING...' : '[S]AVE TASTING'}
                </button>
                <button
                  onClick={() => {
                    setFormData({
                      shop: 'Joelato',
                      flavors: [''],
                      date: new Date().toISOString().split('T')[0],
                      notes: '',
                      person: ''
                    });
                    setShowForm(false);
                  }}
                  className="px-6 py-2 text-black bg-red-400 hover:bg-red-500 border border-red-700 disabled:opacity-50"
                  disabled={isLoading}
                >
                  [C]ANCEL
                </button>
              </div>
            </div>
          </div>
        )}

        {/* Entries List - Grouped by Date */}
        <div className="mb-4">
          <div className="text-white mb-2">
            C:\GELATO&gt; TYPE *.GEL
          </div>
          
          {sortedDates.length === 0 ? (
            <div className="text-red-400 ml-4">
              No tastings found.
              <br />
              0 file(s) displayed.
              <br />
              <br />
              <button 
                onClick={() => setShowForm(true)}
                className="text-cyan-400 hover:text-white underline"
              >
                Run "NEW TASTING" to create first record
              </button>
            </div>
          ) : (
            <div className="space-y-6">
              {sortedDates.map(date => (
                <div key={date} className="border border-cyan-400 p-3 bg-gray-900">
                  <div className="text-yellow-400 mb-3">
                    â”Œâ”€ {new Date(date).toLocaleDateString('en-US', { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' }).toUpperCase()} â”€â”
                  </div>
                  
                  {/* Group entries by shop within this date */}
                  {Array.from(new Set(groupedEntries[date].map(e => e.shop))).map(shop => {
                    const shopEntries = groupedEntries[date].filter(e => e.shop === shop);
                    const people = Array.from(new Set(shopEntries.map(e => e.person)));
                    
                    return (
                      <div key={shop} className="mb-4 ml-4">
                        <div className="text-green-400 mb-2">
                          SHOP: {shop}
                          <span className="text-cyan-400 ml-4">
                            TASTED BY: {people.join(', ')}
                          </span>
                        </div>
                        
                        <div className="flex flex-wrap gap-2 mb-3">
                          {shopEntries.map(entry => (
                            <div key={entry.id} className="relative group">
                              <div className="flavor-tag">
                                {entry.flavor}
                                <button
                                  onClick={() => deleteEntry(entry.id)}
                                  className="ml-2 text-red-400 hover:text-red-300 text-xs"
                                  title="Delete this flavor"
                                >
                                  [x]
                                </button>
                              </div>
                            </div>
                          ))}
                        </div>
                        
                        {shopEntries[0]?.notes && (
                          <div className="text-cyan-400 mt-2">
                            NOTES: {shopEntries[0].notes}
                          </div>
                        )}
                      </div>
                    );
                  })}
                  
                  <div className="text-yellow-400 mt-2">
                    â””{'â”€'.repeat(70)}â”˜
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>

        {/* Command Prompt */}
        <div className="mt-6 text-green-400">
          <div className="flex items-center">
            <span>C:\GELATO&gt;</span>
            <span className="blink ml-1">â–ˆ</span>
            <span className="ml-2 text-sm text-gray-400">
              {isLoading ? 'Processing...' : 'Ready'}
            </span>
          </div>
          <div className="text-xs text-gray-500 mt-2">
            Flavors Today: {entries.filter(e => e.date === new Date().toISOString().split('T')[0]).length} | 
            Total Flavors: {entries.length}
          </div>
        </div>

        {/* Footer Status Bar */}
        <div className="fixed bottom-0 left-0 right-0 bg-cyan-900 text-cyan-300 px-4 py-2 border-t border-cyan-700">
          <div className="flex justify-between items-center">
            <div className="flex gap-4">
              <span>F1=HELP</span>
              <span>F2=SAVE</span>
              <span>F3=LOAD</span>
              <span>F5=REFRESH</span>
              <span>F10=QUIT</span>
            </div>
            <div className="flex gap-4">
              <span className={isLoading ? 'text-yellow-400 animate-pulse' : 'text-green-400'}>
                DB: {isLoading ? 'SYNCING' : 'ONLINE'}
              </span>
              <span>FLAVORS: {entries.length}</span>
              <span>DATES: {sortedDates.length}</span>
              <span>FILTER: {filter.toUpperCase()}</span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default IceCreamTracker;
